    private Pattern phonePattern = Pattern.compile("^(?:\\+\\d{1,3}|0\\d{1,3}|00\\d{1,2})?(?:\\s?\\(\\d+\\))?(?:[-\\/\\s.]|\\d)+$");
    private Pattern URLPattern = Pattern.compile("\\b(https?|ftp|file)://[-a-zA-Z0-9+&@#/%?=~_|!:,.;]*[-a-zA-Z0-9+&@#/%=~_|]");

    public Stream<String> getContacts(Stream<String> total){
        return total.filter(contact -> emailPattern.matcher(contact).matches() || phonePattern.matcher(contact).matches());
    }

    public Stream<String> getWebsites(Stream<String> total){
        return total.filter(URL -> URLPattern.matcher(URL).matches());
    }

    public Stream<String> getText(Stream<String> total){
        return total.filter(text -> !emailPattern.matcher(text).matches() || !phonePattern.matcher(text).matches() || !URLPattern.matcher(text).matches());
    }

    public Stream<String> getImage(Stream<String> total){
        return total.filter(URL -> URLPattern.matcher(URL).matches() && URL.contains(".png") || URL.contains(".jpg"));
    }

