        public static bool isContact(String text)
        {
            return emailPattern.IsMatch(text) || phonePattern.IsMatch(text);
        }

        public static bool isLink(String text)
        {
            return URLPattern.IsMatch(text);
        }

        public static bool isPhoneNumber(String text)
        {
            return phonePattern.IsMatch(text);
        }
        

